  `대중교통정보` varchar(1000) DEFAULT NULL,
  `대중교통위치주소` varchar(255) DEFAULT NULL,
  `대중교통위치 도로` varchar(255) DEFAULT NULL,
  `어린이보호구역` varchar(255) DEFAULT NULL,
  `대중교통위치 위도` double DEFAULT NULL,
  `대중교통위치 경도` double DEFAULT NULL,
  PRIMARY KEY (`번호`),
  KEY `idx_구군` (`구군`),
  KEY `idx_대중교통위치주소` (`대중교통위치주소`)
